<?php	header("Content-Type: application/json");include("conexi.php");include("conexi_remote.php");$link_remote = conectar_remote();$link_local = Conectarse();$respuesta= array();$archivados_remote = array();$archivados_local = array();$q_archivados = "SELECT * FROM doctores 	WHERE archivado = 1";$result_archivados= mysqli_query($link_remote, $q_archivados);$count_archivados = mysqli_num_rows($result_archivados);if($result_archivados){	if($count_archivados > 0){		while($fila = mysqli_fetch_assoc($result_archivados)){			$archivados_remote[] = $fila["id_doctor"];		}		$respuesta["mensaje_archivados_remote"] = $count_archivados. " por archivar"; 	}	else{		$respuesta["mensaje_archivados_remote"] = "No hay doctores por archivar";	}	$respuesta["archivados"] =$archivados_remote;	$respuesta["cant_archivados_remote"] = sizeof($archivados_remote);}else{	$respuesta["estatus_buscar_archivados"] = "error";	$respuesta["mensaje_buscar_archivados"] = "Error en : $q_archivados ".mysqli_error($link_remote);}$string_archivados = "'". implode("','", $archivados_remote)."'";$respuesta["string_archivados"]  = $string_archivados;$archivar_doctores = "UPDATE doctores 		SET archivado  = 1		WHERE id_doctor IN(". $string_archivados. ") ";			if(mysqli_query($link_local, $archivar_doctores)){				$respuesta["archivar_doctor_estatus"] = "success";		$respuesta["archivar_doctor_mensaje"] = "Doctor Archivado";		$respuesta["archivar_doctores"] = $archivar_doctores ;	}	else{		$respuesta["archivar_doctor_estatus"] = "error";		$respuesta["archivar_doctor_mensaje"] = "Error en : $archivar_doctores " . mysqli_error($link_local);	}		$respuesta["cant_archivados_local"] = mysqli_affected_rows($link_local);			echo json_encode($respuesta);?>	