<?phpinclude("conex.php");$link = Conectarse();$respuesta= array();//session_start(); $id_master =$_POST["id_master"];$id_slave =$_POST["id_slave"];$update_slave = "UPDATE ordenes			SET 	id_doctor = '$id_master',	is_mod = 1 ,	fecha_mod = NOW()		WHERE id_doctor = '$id_slave'";	if(mysql_query($update_slave)){	$respuesta["update_slave_estatus"] = "success";	$respuesta["update_slave_mensaje"] = "Doctor actualizado";}else{		$respuesta["update_slave_estatus"] = "error";	$respuesta["update_slave_mensaje"] = "Error en : $update_slave " . mysql_error();}$archivar_doctor = "UPDATE doctores SET archivado  = 1,is_mod = 1WHERE id_doctor= '$id_slave'";if(mysql_query($archivar_doctor)){			$respuesta["archivar_doctor_estatus"] = "success";	$respuesta["archivar_doctor_mensaje"] = "Doctor Archivado";}else{	$respuesta["archivar_doctor_estatus"] = "error";	$respuesta["archivar_doctor_mensaje"] = "Error en : $update_slave " . mysql_error();}					echo json_encode($respuesta);?>	