<?phpinclude("conex.php");$link = Conectarse(); include("is_selected.php");?><?php//echo var_dump($_POST["filtros"]);$zona = $_POST["zona"];$offset = $_POST["offset"];$q_doctores = "SELECT	*FROM	doctoresLEFT JOIN (	SELECT		count(id_orden) AS cant_ordenes,		id_doctor  	FROM		ordenes		GROUP BY		id_doctor) AS ordenes_por_doctor USING (id_doctor)LEFT JOIN zonas ON zonas.id_zona = doctores.zonaWHERE archivado <> 1";if($zona != 'TODAS'){ 	$q_doctores.= " AND zona = '$zona'  ";}else{		}if(isset($_POST["filtros"])){			foreach($_POST["filtros"] as $index => $filtro ){		if(is_array( $filtro)){			if( $filtro["valor_filtro"] != ''){							$q_doctores.=" AND ";				if($filtro["operador_filtro"] == "LIKE"){					$q_doctores.= $filtro["campo_filtro"] . " ". $filtro["operador_filtro"] ." '%". $filtro["valor_filtro"]."%'";				}				else{					$q_doctores.= $filtro["campo_filtro"] . " ". $filtro["operador_filtro"] ." '". $filtro["valor_filtro"]."'";				}			} 		} 	} }	$q_doctores.= " ORDER BY nombre_doctor";$q_doctores.= " LIMIT 100 ";$q_doctores.= " OFFSET $offset ";$result_doctores = mysql_query($q_doctores,$link) or die("Error en: $q_doctores  ".mysql_error());//echo $q_doctores;if(mysql_num_rows($result_doctores)==0){	echo "<tr ><td colspan='7'><div class='alert alert-warning'> No Hay Doctores</div> </td></tr> ";}else{?>	<div class="row hide">				<div class=" col-sm-3">			<h4>Lista de Doctores: <?php echo mysql_num_rows($result_doctores). $q_doctores; ?></h4>					</div>		<div class="col-sm-offset-6 col-sm-2">			<button class="btn btn-primary" onclick="javascript:window.print();">				<i class="fa fa-print"></i> Imprimir			</button>		</div>				</div>							<?php		$num = 0;		while($row = mysql_fetch_assoc($result_doctores)){			$num++;			$id_doctor = $row["id_doctor"];			$nombre_doctor = $row["nombre_doctor"];			$tel_doctor = $row["tel_doctor"];			$zona_doctor = $row["zona"];			$cant_ordenes = $row["cant_ordenes"];					?>			<tr>				<td colspan="6">					<?php						if(isset($_POST["filtros"])){							//echo var_dump($_POST["filtros"]);						}					?>				</td>			</tr>			<tr>				<td> <?php echo $num;?>	</td>				<td> <a href="buscar_doctor.php?id_doctor=<?php echo $id_doctor;?>"> <?php  echo $id_doctor;	?> </a> 	</td>								<td >					<?php echo $nombre_doctor;?>				</td>				<td>					<?php echo $tel_doctor;?>				</td>				<td>					<?php echo $zona_doctor;?>				</td>				<td>					<?php echo $cant_ordenes;?>				</td>  				<td>					<button  class="btn btn-info btn_fusion" data-id_doctor="<?php echo $id_doctor;?>"data-nombre_doctor="<?php echo $nombre_doctor;?>"  type="button" title="Fusionar"  >						<i class="fa fa-exchange"></i>					</button>					<button  class="btn btn-warning btn_edit" data-id_value="<?php echo $id_doctor;?>"  type="button"  >						<i class="fa fa-edit"> </i> 					</button>					<button  class="btn btn-danger btn_delete"  data-id_value="<?php echo $id_doctor;?>" type="button"  >						<i class="fa fa-times"> </i>						</button>				</td>			</tr>		<?php		}		?>	<?php	}		//echo var_dump($_POST["filtros"]);	?>		