<?phpinclude("login/login_success.php");include("conex.php");$link = Conectarse();include("is_selected.php");?><?php$fecha_inicial = $_GET["fecha_inicial"];$fecha_final = $_GET["fecha_final"];$id_doctor = $_GET["id_doctor"];$nombre_doctor = $_GET["nombre_doctor"];$zona = $_GET["zona"];$q_doctores = "SELECT	*FROM	ordenes	LEFT JOIN pacientes	ON ordenes.id_paciente = pacientes.id_paciente			WHERE		id_doctor = '$id_doctor'	AND estatus_actual <> 'Cancelada'	AND fecha_elabora BETWEEN STR_TO_DATE(		'$fecha_inicial',		'%d/%m/%Y'	)	AND STR_TO_DATE('$fecha_final', '%d/%m/%Y') ";if($zona != 'TODAS'){	$q_doctores.= " AND zona = '$zona'";}$result_doctores = mysql_query($q_doctores,$link) or die("Error en: $q_doctores  ".mysql_error());//echo $q_doctores;if(mysql_num_rows($result_doctores)==0){	echo "<div class='alert alert-warning'> No Hay Ordenes en este periodo</div>";}else{?>	<div class="row">		<div class=" col-sm-6">			<h4>Ordenes del Doctor: <?php echo $nombre_doctor;?></h4>			<h4>Del: <?php echo $fecha_inicial;?>  Al: <?php echo $fecha_final;?></h4>						<h4>Total de Ordenes: <?php echo mysql_num_rows($result_doctores);?></h4>			<h4>Zona: <?php echo $zona;?></h4>		</div> 		<div class="col-sm-offset-4 col-sm-2">			<button class="btn btn-primary no_imprimir" onclick="javascript:window.print();">				<i class="fa fa-print"></i> Imprimir			</button>		</div>	</div>		<table border="1" >			<th>Folio</th>			<th>Fecha</th>			<th>Detalle</th>			<th>Paciente</th>			<th>Estatus</th>			<th>Total</th>					<?php		$num = 0;		$arr_total = array();		while($row = mysql_fetch_assoc($result_doctores)){			$num++;			$id_orden = $row["id_orden"];						$fecha_orden = date("d/m/Y", strtotime($row["fecha_elabora"]));			$estatus = $row["estatus_actual"];			$nombre_paciente = $row["nombre_paciente"];			$importe_total = $row["importe_total_orden"];			$arr_total[] =  $importe_total;		?>			<tr>								<td> 					<a href="detalle_orden.php?id_orden=<?php echo $id_orden;?>"> <?php  echo $id_orden;	?> </a> 					</td>				<td>					<?php echo $fecha_orden;?>				</td>				<td>					<?php						$q_detalle="SELECT CONCAT(tipo_estudio, ': ', nombre_estudio) as nombre_estudio FROM detalle_orden INNER JOIN estudios						ON detalle_orden.id_estudio = estudios.id_estudio WHERE id_orden = '$id_orden'";						$result_detalle = mysql_query($q_detalle,$link) or die("Error en $q_detalle: ".mysql_error());						while($row = mysql_fetch_assoc($result_detalle)){							$nombre_estudio= $row["nombre_estudio"];							echo $nombre_estudio. '<br/>';						}						?>				</td>				<td>					<?php echo $nombre_paciente;?>				</td>								<td>					<?php echo $estatus;?>				</td>				<td class="text-right">					<?php echo $importe_total;?>				</td>							</tr>		<?php		}		?>			<tr class="fila_totales">				<td colspan="5">Total</td>				<td> $ <?php echo number_format(array_sum($arr_total), 2);?></td>			</tr>			<tr >				<td colspan="5">Comisi√≥n 10%</td>				<td> $ <?php echo number_format((array_sum($arr_total) * .1), 2);?></td>			</tr>		</table>	<?php	}	?>