rjeta"];			$estatus_actual = $row["estatus_actual"];						if($estatus_actual != "Cancelada"){				if( $medio_pago == 'Efectivo'){				$efectivo = $anticipo ;				$subtotal_efectivo[] = $anticipo;				}				elseif($medio_pago == 'Tarjeta'){					$tarjeta = $anticipo ;					$subtotal_tarjeta[] = $anticipo;				}				elseif($medio_pago == 'Mixto'){										$subtotal_tarjeta[] = $tarjeta;					$subtotal_efectivo[] = $efectivo;				}			}		?>			<div class="row row-border">				<div class="col-sm-1 col-xs-3"> <?php echo $num;?>	</div>				<div class="col-sm-1 col-xs-8 text-center"> 						<a href="detalle_orden.php?id_orden=<?php echo $id_orden;?>"> <?php  echo $id_orden;	?> </a> 				</div>				<div class="col-sm-3 col-xs-8">					<?php						$q_detalle="SELECT CONCAT(tipo_estudio, ': ', nombre_estudio) as nombre_estudio FROM detalle_orden INNER JOIN estudios						ON detalle_orden.id_estudio = estudios.id_estudio WHERE id_orden = '$id_orden'";						$result_detalle = mysql_query($q_detalle,$link) or die("Error en $q_detalle: ".mysql_error());						while($row = mysql_fetch_assoc($result_detalle)){							$nombre_estudio= $row["nombre_estudio"];							echo $nombre_estudio. '<br/>';						}						?>				</div>				<div class="col-sm-1 col-xs-4 text-right">					<span><?php echo $anticipo;?></span>				</div>				<div class="col-sm-1 hidden-xs">					<span class="visible-xs pull-left"><b>Efectivo: </b></span> <?php echo " $ ". $efectivo;?>				</div>				<div class="col-sm-1 hidden-xs">					<span class="visible-xs pull-left"><b>Tarjeta: </b></span> <?php echo "$". $tarjeta;?>				</div>				<div class="col-sm-2 hidden-print">					<?php echo "$estatus_actual";?>				</div>			</div>		<?php					}						?>		</div>				<br>		<br>		<table class="table-bordered">			<tr>				<td>					Total de Estudios:				</td>				<td class="text-right">					<?php echo mysql_num_rows($result_totales);?>				</td>			</tr>			<tr>				<td>Total de Efectivo</td>								<td class="text-right"> $					<span class="subtotal_monedas" id="span_total_efectivo">						<?php echo number_format(array_sum($subtotal_efectivo),2);?>					</span>					<input type="hidden" id="total_efectivo" value="<?php echo array_sum($subtotal_efectivo);?>">				</td>			</tr>			<tr>				<td>Total Tarjeta</td>				<td class="text-right">					<span class="subtotal_monedas" id="tarjeta">						<?php echo  "$ ".number_format(array_sum($subtotal_tarjeta),2);?>						<input type="hidden" id="total_tarjeta" value="<?php echo array_sum($subtotal_tarjeta)?>">					</span>				</td>			</tr>			<tr>				<td>Total del Dia <BR>(Efectivo + Tarjeta)</td>				<td class="text-right">					<span class="subtotal_monedas" id="total_dia">					<?php						$total_dia = array_sum($subtotal_tarjeta) + array_sum($subtotal_efectivo);						echo  "$ ".number_format($total_dia, 2);					?>					<input type="hidden" id="total_dia" value="<?php echo $total_dia;?>">					</span>				</td>			 </tr>		  <tr>			<td>Vales Rosas</td>						<td><input class="monedas" type="text" id="txt_vales" ></td>			<td><span class="subtotal_monedas" id="subtotal_vales"> </span></td>		  </tr>		  <tr>			<td>Efectivo Inicial</td>			<td class="text-right">				<span class="subtotal_monedas" id="efectivo_inicial">					<?php echo "$ ".number_format($_SESSION["efectivo"],2);?>				</span>				<input type="hidden" id="efectivo_inicial" value="<?php echo $_SESSION["efectivo"];?>">							</td>		  </tr>		  <tr>			<td>Efectivo Final<BR> (Efectivo- Vales)</td>			<td>				<span class="subtotal_monedas" id="span_efectivo_final">										<?php echo number_format(array_sum($subtotal_efectivo) , 2);?>				</span>				<input type="hidden" id="efectivo_final" value="<?php echo array_sum($subtotal_efectivo);?>">			</td>		  </tr>		</table>		<?php	}	?>