<?phpinclude("login/login_success.php");include("conex.php");$link = Conectarse();include("is_selected.php");	$f_inicial = $_GET["fecha_inicial"];	$f_final = $_GET["fecha_final"];	$zona = $_GET["zona"];				if($zona == 'TODAS'){			$q_totales="SELECT 		zonas.zona, 		SUM(importe_total_orden) AS importe_total ,		COUNT(id_orden) AS num_ordenes, 		nombre_doctor, ordenes.id_doctor  		FROM ordenes		LEFT JOIN doctores		USING (id_doctor)		LEFT JOIN zonas ON doctores.zona= zonas.id_zona		WHERE fecha_elabora		BETWEEN str_to_date('$f_inicial', '%d/%m/%Y') 		AND  str_to_date('$f_final', '%d/%m/%Y')		AND estatus_actual <> 'Cancelada'		GROUP BY ordenes.id_doctor";	}else{		$q_totales="SELECT 		zonas.zona, 		SUM(importe_total_orden) AS importe_total , 		COUNT(id_orden) AS num_ordenes, 		nombre_doctor, ordenes.id_doctor 		FROM ordenes		LEFT JOIN doctores 		USING (id_doctor)		LEFT JOIN zonas ON doctores.zona= zonas.id_zona		WHERE fecha_elabora		BETWEEN str_to_date('$f_inicial', '%d/%m/%Y') 		AND  str_to_date('$f_final', '%d/%m/%Y')		AND  doctores.zona = '$zona'		AND estatus_actual <> 'Cancelada'		GROUP BY ordenes.id_doctor";	}		$result_totales=mysql_query($q_totales,$link) or die("Error en: $q_totales  ".mysql_error());			if(mysql_num_rows($result_totales)==0){		echo "<div class='alert aler-warning'> No Hay Registros</div>";		exit();	}			?>  		<div class="row">		<div class="col-sm-8">			<h4>				Reporte de Comisiones <?php echo $f_inicial. " al " .$f_final .", ZONA: ". $zona;?>			</h4>		</div>		<div class="col-sm-3 pull-right hidden-print">						<button class="btn btn-default btn_exportar" type="button">					<i class="fa fa-file-excel-o"></i> Exportar				</button>				<button class="btn btn-primary" onclick="window.print();">					<i class="fa fa-print"></i> Imprimir				</button>		</div>			</div>	<table border="1" id="tabla_reporte">		<thead>			<th>TOTAL DE ORDENES</th>			<th>DOCTOR</th>			<th>ZONA</th>			<th>IMPORTE</th>			<th>COMISION</th>		</thead>		<tbody>			<?php		$total_ordenes = array();		while($row = mysql_fetch_assoc($result_totales)){			$num_ordenes = $row["num_ordenes"];			$doctor = $row["nombre_doctor"];			$id_doctor = $row["id_doctor"];			$importe_total = $row["importe_total"];			$zona = $row["zona"];			$total_ordenes[] = $num_ordenes ;			?>			<tr>				<td>					<?php echo "$num_ordenes";?>				</td>				<td>					<a href="buscar_doctor.php?id_doctor=<?php echo $id_doctor;?>"> 							<?php echo $doctor;?>					</a>				</td>				<td> <?php echo $zona;?></td>				<td><?php echo "$importe_total";?></td>				<td>					<?php echo "$ ".$importe_total*.1;	?>				</td>			</tr>		<?php 		}		?>		<tr>				<td>					<?php echo array_sum($total_ordenes);?>				</td>				<td><?php echo mysql_num_rows($result_totales);?></td>				<td></td>				<td></td>				<td></td>						</tr>	</tbody>		</table>