<?php	header('Content-Type: application/json');	include("conex.php");	$link=Conectarse();		$response = array();	$debug = array();			$table = $_GET["table"];	$id_field = $_GET["id_field"];	$id_value = $_GET["id_value"];	$mensaje_error = $_GET["mensaje_error"];			//$query_complete = "SELECT $campo FROM $tabla  ";	//$query_complete = "SELECT $valor, $etiqueta,  FROM $tabla WHERE $etiqueta LIKE '%" . $q . "%'";	$query_complete = "SELECT *  FROM $table WHERE $id_field = '$id_value'";		$result_complete = mysql_query( $query_complete, $link )	or die("Error al ejecutar consulta: $query_complete".MYSQL_ERROR());		$count_rows = mysql_num_rows($result_complete);	$count = 0;	while($row = mysql_fetch_assoc($result_complete)) {		$count++;		$response["data"] = $row ;			}		$response["count_rows"] = "$count_rows" ;		$response["mensaje"] =  $count_rows < 1 ? $mensaje_error : "OK";	$response["query"] =  $query_complete;			print(json_encode($response));	?>