<?phpif(isset($_POST["iniciar"])){			include("../conex.php");			$link=Conectarse();			$myusername=$_POST['myusername']; 			$mypassword=$_POST['mypassword']; 			if($_POST['efectivo'] <> '') {				$efectivo=$_POST['efectivo']; 							}			else {				$efectivo=0;			}						// To protect MySQL injection (more detail about MySQL injection)			$myusername = stripslashes($myusername);			$mypassword = stripslashes($mypassword);			$myusername = mysql_real_escape_string($myusername);			$mypassword = mysql_real_escape_string($mypassword);			$sql="SELECT * FROM usuarios				WHERE usuario='$myusername' 				AND pass='$mypassword'";							$result=mysql_query($sql,$link);				if (!$result){					 die('Error: ' . mysql_error());				}							$count=mysql_num_rows($result);			// Si la consulta devuelve 1 fila inicia la sesion			if($count==1){								session_start();				session_regenerate_id(true);				$a = session_id();				$row = mysql_fetch_assoc($result);				$id_usuario = $row["id_usuario"];				$nombre_usuario= $row["nombre_usuario"];				$_SESSION["usuario"] = $myusername or die("Error al iniciar variables de sesión");				$_SESSION["sucursal"] = $row["sucursal"] or die("Error al iniciar sucursal");				$_SESSION["nombre_usuario"] = $row["nombre_usuario"] or die("Error al iniciar sucursal");				$_SESSION["permisos"] = $row["permisos"] or die("Error al iniciar permisos");				$_SESSION["efectivo"] = $efectivo or die("Error en efectivo");								$q_iniciar_turno = "INSERT INTO turnos SET id_sesion = '$a',				fecha_login = CURDATE(),				hora_login= CURTIME(),				id_usuario= '$id_usuario'";								mysql_query($q_iniciar_turno) or die("Error al guardar pago: $$q_iniciar_turno ".mysql_error());								header("location:../index.php");															}		}?><html>	<head>	<title>		Iniciar Sesión	</title>	<link rel="stylesheet" href="../css/layout.css" type="text/css"/>			</head>	<body>	<div class="contenido">		<table width="300" border="0" align="center" cellpadding="0" cellspacing="1" bgcolor="#CCCCCC">			<tr>			<form name="form_login" method="post" action="<?php echo $_SERVER['PHP_SELF']?>">				<td>				<table width="100%" border="0" cellpadding="3" cellspacing="1" bgcolor="#FFFFFF">					<tr>						<td colspan="3"><strong>Iniciar Sesión</strong></td>					</tr>					<tr>						<td width="78">Usuario</td>						<td width="6">:</td>						<td width="294">						<!--							<select id="select_usuarios">							<option value="">							</option>						<select/>						-->												<input name="myusername" type="text" id="myusername"></td>					</tr> 					<tr>						<td>Contraseña</td>						<td>:</td>						<td><input name="mypassword" type="password" id="mypassword" /></td>					</tr>					<!--					<tr>						<td>Tipo de Sesión </td>						<td>:</td>						<td>						<select>							<option value="administrador">Administrador							</option>							<option value="cajero">Cajero							</option>						</select>												</td>					</tr>					-->					<tr>						<td>Efectivo en Caja</td>						<td>:</td>						<td><input name="efectivo" type="text"  id="efectivo" value="500" /></td>					</tr>					<tr>						<td>&nbsp;</td>						<td>&nbsp;</td>						<td><input type="submit" name="iniciar" value="Iniciar" /></td>					</tr>				</table>				</td>			</form>			</tr>		</table>				<?php 		if(isset($count) && $count != 1){			echo "<div id='error_login'>Usuario y/o Contraseña inválidos</div>";		}						?>		</div>	</body></html>