<?phpheader("Content-Type: application/json");$table = $_POST["table"];$id_field = $_POST["id_field"];$rows_unsync = $_POST["rows_unsync"];$response = array();if(count($rows_unsync )> 0){		include("conex_remote.php");		$db_conex =  Conectarse();		$response["db_conex"] = json_decode($db_conex);		$obj_db_conex = $response["db_conex"];	//var_dump($response);					if($obj_db_conex ->status == "ok"){		$count_success = 0;				foreach($rows_unsync as $this_row ){			$str_pairs = "";						foreach($this_row as $key=>$value){				$str_pairs.= $key . " = '" . $value. "',";							}			$str_pairs  = trim($str_pairs, ",");						$insert_row_unsync="INSERT IGNORE INTO $table SET $str_pairs ";						$result = mysql_query($insert_row_unsync);			if(!$result){								$response["query_status"]= "Error ";				$response["query_mensaje"] = "Error en: $insert_row_unsync".mysql_error();			}			else{				$count_success++;				$response["synced_ids"][]  = $this_row["$id_field"];				$response["ordenes_subidas"] = $count_success;							}			}				// actualiza id insertadas correctamente a sync = 1;							}		}echo json_encode($response);/* 		while($row = mysql_fetch_array($result)) {			setlocale(LC_ALL,"es_ES");			$fecha_entrega = date("d/m/Y", strtotime($row["fecha_entrega"])); 			$fecha_elabora = date("d/m/Y", strtotime($row["fecha_elabora"])); 			$hora_elabora = $row["hora_elabora"]; 			$estatus_actual = $row["estatus_actual"]; 			$sucursal = $row["sucursal"]; 			$enviar_consultorio = $row["enviar_consultorio"]; 			$elabora = $row["elabora"]; 			$nombre_doctor = $row["nombre_doctor"]; 			$nombre_paciente = $row["nombre_paciente"]; 			$sexo = $row["sexo"]; 			$edad = $row["edad"]; 			$tel_paciente = $row["telefono_paciente"]; 						$anticipo = $row["anticipo"];			$restante = $row["restante"];			$descuento = $row["descuento"];			$medio_pago = $row["medio_pago"];			$importe_total = $row["importe_total_orden"];			$recibo_hono