<?phpinclude "login/login_success.php";include ("conexi.php");$link = Conectarse();$q_zonas= "SELECT * FROM zonas ORDER BY zona";	$result_zonas = mysqli_query($link, $q_zonas );	$dt_fecha_inicial= new DateTime("first day of this month");$dt_fecha_final = new DateTime("last day of this month");$fecha_inicial = $dt_fecha_inicial->format("d/m/Y");$fecha_final = $dt_fecha_final->format("d/m/Y");?> <html ><head>	<title>Reporte de Comisiones</title>	<meta charset="utf-8" />	<?php include("styles.php");?>	</head><body><div class="container">	<?php		include("header.php");		//include("menu_busqueda.php");	?>			<div class="subtitulo" >			Reporte Mensual de Comisiones		</div>				<div class="row no_imprimir">			<div class=" col-sm-7 ">				<form id="form_filtro" class="form-inline" role="form" method="GET" enctype="multipart/form-data" 	>									<div class="form-group">						<label>FECHA INICIAL: </label>						<input size="10" class="form-control"  name="fecha_inicial" id="fecha_inicial" value="<?php echo $fecha_inicial;?>" />					</div>									<div class="form-group">						<label>FECHA FINAL: </label>						<input size="10" class="form-control"  name="fecha_final" id="fecha_final" value="<?php echo $fecha_final;?>" />					</div>					<div class="form-group">						<label>Zona: </label>											<?php							if($result_zonas){ ?>								<select class="form-control filtro_select" name="zona">									<option value="TODAS" selected="selected">TODAS	</option>									<?php									while($fila_zonas = mysqli_fetch_assoc($result_zonas)){ 										?>													<option value="<?php echo $fila_zonas["id_zona"];?>"> 													<?php echo $fila_zonas["zona"];?>												</option>																		<?php									}									?>								</select>																					<?php									}																?>					</div>				</form>			</div>						<div class="col-sm-2">				<button  class="btn btn-success " id="btn_reporte" type="button"  >					<i class="fa fa-search"> </i> Generar 	<i id="load_reporte" class="fa fa-spin fa-spinner hide"> </i>				</button>			</div>					</div>		<div id="div_reporte">						</div>		<?php include("footer.php");?></div>		<?php include("scripts.php");?>		<script>		$(document).ready(function(){											$( "#fecha_inicial" ).datepicker({				changeMonth: true,				changeYear: true,				//yearRange: '1920:2000'			});			$( "#fecha_final" ).datepicker({				changeMonth: true,				changeYear: true,				//yearRange: '1920:2000'			});						$("#btn_reporte").click( function cargarReporte(){				$("#load_reporte").toggleClass("hide");								$("#div_reporte").load("get_reporte_comisiones.php", 					$("#form_filtro").serialize(),					function(response, success){						$("#load_reporte").toggleClass("hide");												$(".btn_exportar").click( function exportar(){							console.log("Exportando");							$('#tabla_reporte').tableExport(							{								type:'excel',								tableName:'Reporte',								escape:'false'							});						});																});			});						$("#btn_reporte").click();					});				function validar(){			if ($("#fecha_inicial").val() == ''){				alert("Ingresa una Fecha Inicial") ;				return false;			}			if ($("#fecha_final").val() == ''){				alert("Ingresa una Fecha Final") ;				return false;			}		}	</script></body></html>	