<?phpheader("Content-Type: application/json");include("conex.php");$link=Conectarse();$table = $_GET["table"];$id_field = $_GET["id_field"];$id_value= $_GET["id_value"];$response = array();$debug = array();$query = "SELECT *  FROM $table WHERE $id_field = '$id_value'";$result = mysql_query( $query, $link )or die("Error al ejecutar consulta: $query".MYSQL_ERROR());$found= mysql_num_rows( $result);$response["found"] = $found;$count = 0;while($row = mysql_fetch_assoc($result)) {	$count++;	$response["data"] =  $row ;	}print(json_encode($response));?>