<?php	header("Content-Type : application/json");	include("conex.php");	$link=Conectarse();		class ElementoAutocompletar {	   var $value;	   var $label;	   var $tel;	   var $dir;	   var $zona;	   	   function __construct($value, $label, $tel, $dir, $zona ){			$this->value = $value;			$this->label = $label;			$this->tel = $tel;			$this->dir = $dir;			$this->zona = $zona;	   }	}		$tabla = $_GET["tabla"];	$campo = $_GET["campo"];	$id="id_doctor";	$valor = $_GET["valor"];	$etiqueta = $_GET["etiqueta"];	$q = $_GET["term"];	$response = array();		$query_complete = "SELECT *  FROM $tabla WHERE $etiqueta LIKE '%" . $q . "%'";		$result_complete = mysql_query( $query_complete, $link )	or die("Error al ejecutar consulta: $query_complete".MYSQL_ERROR());		while($row = mysql_fetch_array($result_complete)) {		$label = $row["$etiqueta"];		$value = $row["$valor"];		$tel = $row["tel_doctor"];		$dir = $row["dir_doctor"];		$zona = $row["zona"];		//$response["value"] = $id ;		//$response["label"] = $nombre ;		array_push($response, new ElementoAutocompletar($value, $label, $tel, $dir, $zona));	}			//return (json_encode($response));	//print($query_complete);	print(json_encode($response));		//ECHO json_encode($response);?>