<?php	include("conex.php");	$link=Conectarse();		class ElementoAutocompletar {		var $value;		var $label;		var $extra1;		var $extra2;				function __construct($value, $label, $extra1, $extra2 ){			$this->value = $value;			$this->label = $label;			$this->extra1 = $extra1;			$this->extra2 = $extra2;		}	}		$tabla = $_GET["tabla"];	$campo = $_GET["campo"];	$valor = $_GET["valor"];	$etiqueta = $_GET["etiqueta"];	$extra1 = $_GET["extra1"];	$extra2 = $_GET["extra2"];	$q = $_GET["term"];	$response = array();	$debug = array();		//$query_complete = "SELECT $campo FROM $tabla  ";	//$query_complete = "SELECT $valor, $etiqueta,  FROM $tabla WHERE $etiqueta LIKE '%" . $q . "%'";	$query_complete = "SELECT *  FROM $tabla WHERE $campo LIKE '%" . $q . "%'";		$result_complete = mysql_query( $query_complete, $link )	or die("Error al ejecutar consulta: $query_complete".MYSQL_ERROR());		while($row = mysql_fetch_assoc($result_complete)) {		$label = $row["$etiqueta"];		$value = $row["$valor"];		$campo1 = $row["$extra1"];		$campo2 = $row["$extra2"];				//$response["value"] = $id ;		//$response["label"] = $nombre ;		array_push($response, new ElementoAutocompletar($value, $label, $campo1, $campo2));		//array_push($response, array($row["$extra1"] , $row["$extra2"])  );	}			//return (json_encode($response));	//print($query_complete);	header('Content-Type: application/json');	print(json_encode($response));		//ECHO json_encode($response);?>