<?php	header('Content-Type: application/json');	include("conex.php");	$link=Conectarse();		$response = array();	$debug = array();		$term = $_GET["term"];		$tabla = $_GET["tabla"];	$campo = $_GET["campo"];	$valor = $_GET["valor"];	$etiqueta = $_GET["etiqueta"];		//$arr_pairs = $_GET["fields_value"];	//$value = $_GET["value"];		//$query_complete = "SELECT $campo FROM $tabla  ";	//$query_complete = "SELECT $valor, $etiqueta,  FROM $tabla WHERE $etiqueta LIKE '%" . $q . "%'";	$query_complete = "SELECT *  FROM $tabla WHERE $campo LIKE '%" . $term . "%'";	if($tabla == "doctores"){		$query_complete.= " AND archivado <> 1";	}		$result_complete = mysql_query( $query_complete, $link )	or die("Error al ejecutar consulta: $query_complete".MYSQL_ERROR());		$count = 0;	while($row = mysql_fetch_assoc($result_complete)) {		$count++;		array_push($response, array("value" => $row["$campo"], "label" => $row["$campo"], "extras" => $row ));			}		print(json_encode($response));?>