<?phpif(isset($_POST["guardar_todo"])){			$estatus =$_POST["estatus"];	$causas = $_POST["causas"];	$count=0;	foreach ($estatus as $key=>$value){			$insert_historial = "INSERT INTO historial_ordenes				SET id_orden = '$key',		estatus = '$estatus[$key]',		observaciones = '$causas[$key]',		fecha_hist = CURDATE(),		hora_hist = CURTIME()";				mysql_query($insert_historial) or die("Error en : $insert_historial " . mysql_error());				$update_historial = "UPDATE ordenes 		SET estatus_actual  = '$estatus[$key]'		WHERE id_orden='$key'";		mysql_query($update_historial) or die("Error en : $update_historial " . mysql_error());		$count ++;				}	echo"	<div class='usua' id=\"div_mensaje_orden\">		$count Registros actualizados correctamente	</div>";	}?>	