<?phpheader("Content-Type: application/json");$synced_ids = $_POST["synced_ids"];$table = $_POST["table"];$id_field = $_POST["id_field"];include("conex.php");	$db_conex =  Conectarse();$count_updated = 0;foreach( $synced_ids as $id_value){		$update_synced="UPDATE $table SET sync = 1 WHERE $id_field = '$id_value' ";		$result = mysql_query($update_synced);	if(!$result){				$response["query_status"]= "Error ";		$response["query_mensaje"] = "Error en: $update_synced".mysql_error();	}	else{		$count_updated++;		$response["ordenes_actualizadas"] = $count_updated;	}		}echo json_encode($response);?>